mapper.select:
select id, owner, name, startDate, endDate, cost, description
from tbl_activity
where id=#{id}

mapper.delete:
delete from tbl_activity

where id in


    #{id}

mapper.select:
select count(*) from tbl_activity a join tbl_user u on a.owner = u.id
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.endDate < #{endDate}

select a.id, a.name, u.name as owner, a.startDate, a.endDate from tbl_activity as a join tbl_user as u on a.owner = u.id
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.endDate < #{endDate}
order by a.startDate desc
limit #{skipCount},#{pageSize}
